import type {
  ButtonConfig,
  IconConfig,
  DividerConfig,
  SpaceConfig,
  RowConfig,
  InputConfig,
  TextareaConfig,
  RateConfig,
  BadgeConfig,
  CardConfig,
  CollapseConfig,
  ImageConfig,
  StatisticConfig,
  MessageConfig,
  ResultConfig,
  SpinConfig,
  ProgressConfig,
  BackTopConfig,
  GlobalConfig,
  TooltipConfig,
  PopoverConfig,
  SelectConfig,
  RadioGroupConfig,
} from './types'

import { shallowReactive } from 'vue'
import { numFormatter } from './numFormatter'

// --------------------- General ---------------------
const button = shallowReactive<ButtonConfig>({ mode: 'default', size: 'medium' })

const icon = shallowReactive<IconConfig>({})

// --------------------- Layout ---------------------
const divider = shallowReactive<DividerConfig>({
  dashed: false,
  plain: false,
  position: 'center',
  type: 'horizontal',
})

const space = shallowReactive<SpaceConfig>({ size: 'small' })

const row = shallowReactive<RowConfig>({ wrap: true })

// --------------------- Navigation ---------------------

// --------------------- Data Entry ---------------------
const input = shallowReactive<InputConfig>({
  size: 'medium',
  clearable: false,
  borderless: false,
})

const textarea = shallowReactive<TextareaConfig>({
  resize: 'vertical',
  autoRows: false,
  showCount: false,
  size: 'medium',
  clearable: false,
})

const rate = shallowReactive<RateConfig>({
  count: 5,
  icon: 'star',
  allowHalf: false,
  allowClear: false,
})

const radioGroup: RadioGroupConfig = shallowReactive({
  size: 'medium',
  mode: 'border',
})

const select = shallowReactive<SelectConfig>({
  borderless: false,
  clearable: false,
  labelKey: 'label',
  searchable: false,
  size: 'medium',
  valueKey: 'value',
})

// --------------------- Data Display ---------------------
const badge = shallowReactive<BadgeConfig>({ showZero: false, dot: false, overflowCount: 99 })

const card = shallowReactive<CardConfig>({
  size: 'medium',
  borderless: false,
  hoverable: false,
})

const collapse: CollapseConfig = shallowReactive<CollapseConfig>({ accordion: false })

const image: ImageConfig = shallowReactive({
  width: 100,
  height: 100,
  fallback:
    'data:image/png;base64,',
})

const statistic = shallowReactive<StatisticConfig>({
  precision: 0,
  formatter: numFormatter,
})

const tooltip = shallowReactive<TooltipConfig>({
  placement: 'top',
  trigger: 'hover',
  showDelay: 100,
  hideDelay: 100,
  destroyOnHide: false,
  autoAdjust: true,
})

const popover = shallowReactive<PopoverConfig>({
  placement: 'top',
  trigger: 'click',
  showDelay: 100,
  hideDelay: 100,
  destroyOnHide: false,
  autoAdjust: true,
})

// --------------------- Feedback ---------------------
const message = shallowReactive<MessageConfig>({
  duration: 3000,
  maxCount: 5,
  top: 60,
  destroyOnHover: false,
})

const result = shallowReactive<ResultConfig>({ status: 'info' })

const spin = shallowReactive<SpinConfig>({
  icon: 'loading',
  tip: '',
  tipAlign: 'vertical',
  size: 'small',
})

const progress = shallowReactive<ProgressConfig>({
  size: 'medium',
  format: (percent: number) => percent + '%',
})

// --------------------- Other ---------------------
const backTop = shallowReactive<BackTopConfig>({
  duration: 450,
  visibilityHeight: 400,
})

// --------------------- end ---------------------

export const defaultConfig: GlobalConfig = {
  // General
  button,
  icon,
  // Layout
  divider,
  space,
  row,
  // Navigation
  // Data Entry
  input,
  textarea,
  rate,
  radioGroup,
  select,
  // Data Display
  badge,
  card,
  collapse,
  image,
  statistic,
  tooltip,
  popover,
  // Feedback
  message,
  result,
  spin,
  progress,
  // Other
  backTop,
  // --- end ---
}
